Scripts.OnClick:
local parent = self:GetParent();
local scrollStep = self:GetParent().scrollStep or (parent:GetHeight() / 2);
parent:SetValue(parent:GetValue() - scrollStep);
PlaySound("UChatScrollButton");

local parent = self:GetParent();
local scrollStep = self:GetParent().scrollStep or (parent:GetHeight() / 2);
parent:SetValue(parent:GetValue() + scrollStep);
PlaySound("UChatScrollButton");

