Scripts.OnVerticalScroll:
local scrollbar = _G[self:GetName().."ScrollBar"];
scrollbar:SetValue(offset);

local min, max = scrollbar:GetMinMaxValues();
if ( offset == 0 ) then
	_G[scrollbar:GetName().."ScrollUpButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollUpButton"]:Enable();
end
if ((scrollbar:GetValue() - max) == 0) then
	_G[scrollbar:GetName().."ScrollDownButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollDownButton"]:Enable();
end

Scripts.OnMouseWheel:
if self:GetParent():IsEnabled() == 1 then
	local amount = IsShiftKeyDown() and 10 or 1;
	if delta > 0 then
		NumericInputSpinner_Increment(self:GetParent(), amount);
	else
		NumericInputSpinner_Decrement(self:GetParent(), amount);
	end
	self:GetParent():ClearFocus();
end

Scripts.OnMouseDown:
if self:IsEnabled() == 1 then
	self.texture:SetPoint("TOPLEFT", self, "TOPLEFT", 1, -1);
end
